def GomtokenTransaction(dataTranfers):
    numThreads = 1
    if numThreads is None:
        try:
            numThreads = os.cpu_count()
        except AttributeError:
            numThreads = 5

    listLock = threading.Lock()
    listOutLock = threading.Lock()
    threads = []
    listDatatranfers = dataTranfers.to_dict('records')
    if dataTranfers["Sym"][0] == "BNB":
        for i in range(numThreads):
            thread = BSC_Threads(listLock, listOutLock, iter(listDatatranfers))
            threads.append(thread)
            thread.start()